      <p>Time and Date: {timeAgo}</p>
      <p>{new Date(sugar.time).toLocaleDateString("en-GB")}</p>
      <span>
        <DeleteIcon onClick={handleClick} />
      </span>
    </div>
  );
};
export default SugarDetails;
