import { useState } from "react";
import { useSugarContext } from "../hooks/useSugarContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { useEffect } from "react";
import { Chart } from "chart.js";
const SugarForm = () => {
  const { dispatch } = useSugarContext();

  const [sugarlvl, setSugarlvl] = useState("");
  const [meal, setMeal] = useState("");
  const [notes, setNotes] = useState("");

  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const { user } = useAuthContext();

  const HbA1cAndGraph = () => {
    const [hba1c, setHbA1c] = useState("");
    const [sugarLogs, setSugarLogs] = useState([]);

    useEffect(() => {
      // Fetch HbA1c
      const fetchHbA1c = async () => {
        const response = await fetch("/api/sugars/hba1c", {
          headers: {
            Authorization: `Bearer ${user.token}`, // Ensure you have the user's token
          },
        });
        const data = await response.json();
        if (response.ok) {
          setHbA1c(data.hba1c);
        }
      };

      // Fetch sugar logs for the last 3 months
      const fetchSugarLogs = async () => {
        const response = await fetch("/api/sugars?timeframe=3months", {
          headers: {
            Authorization: `Bearer ${user.token}`, // Ensure you have the user's token
          },
        });
        const data = await response.json();
        if (response.ok) {
          setSugarLogs(data);
          // Initialize graph here after data is fetched
        }
      };

      fetchHbA1c();
      fetchSugarLogs();
    }, []);

    // Initialize the graph with Chart.js in a useEffect hook based on sugarLogs state

    return (
      <div>
        <h3>HbA1c: {hba1c}</h3>
        <canvas id="sugarGraph"></canvas>{" "}
        {/* This is where the graph will be rendered */}
      </div>
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError("You must be logged in");
      return;
    }

    const newSugar = { sugarlvl, meal, notes };

    const response = await fetch("/api/sugars", {
      method: "POST",
      body: JSON.stringify(newSugar),
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${user.token}`,
      },
    });

    const data = await response.json();

    if (!response.ok) {
      setError(data.error);
      setEmptyFields(data.emptyFields);
    }

    if (response.ok) {
      setSugarlvl("");
      setMeal("");
      setNotes("");
      setError(null);
      setEmptyFields([]);
      console.log("new sugar log added:", data);
      dispatch({ type: "CREATE_SUGAR", payload: data });
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Add a New Sugar Log</h3>
      <label>Sugar Level</label>
      <input
        type="number"
        value={sugarlvl}
        onChange={(e) => setSugarlvl(e.target.value)}
        className={`${emptyFields.includes("sugarlvl") ? "error" : ""}`}
      />
      <label>Meal</label>
      <input
        type="text"
        value={meal}
        onChange={(e) => setMeal(e.target.value)}
        className={`${emptyFields.includes("meal") ? "error" : ""}`}
      />
      <label>Notes</label>
      <input
        type="text"
        value={notes}
        onChange={(e) => setNotes(e.target.value)}
      />
      <div>
        <button>Submit</button>
      </div>

      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default SugarForm;
