import { useEffect } from "react";
import { useState } from "react";

//components
import SugarDetails from "../components/SugarDetails";
import SugarForm from "../components/SugarForm";

const Home = () => {
  const [sugars, setSugars] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("/api/sugars");
      const data = await response.json();

      if (response.ok) {
        setSugars(data);
      }
    };
    fetchData();
  });

  return (
    <div>
      <div className="workouts">
        {sugars &&
          sugars.map((sugar) => <SugarDetails key={sugar.id} sugar={sugar} />)}
      </div>

      <SugarForm />
    </div>
  );
};

export default Home;
